layoutDropdownDebug.code:
// Test Script: Inspect wb-select dropdown options
console.log('=== WB-SELECT DROPDOWN DEBUG ===');

// 1. Check the element
console.log('Element:', element);
console.log('Tag:', element.tagName);
console.log('ID:', element.id);

// 2. Check wb-select internal state
console.log('\n--- Internal State ---');
console.log('_options:', element._options);
console.log('_filteredOptions:', element._filteredOptions);
console.log('_selectedValues:', element._selectedValues);

// 3. Check DOM structure
console.log('\n--- DOM Structure ---');
const dropdown = element.querySelector('.wb-select-dropdown');
console.log('Dropdown element:', dropdown);
console.log('Dropdown display:', dropdown ? getComputedStyle(dropdown).display : 'not found');

const optionsContainer = element.querySelector('.wb-select-options');
console.log('Options container:', optionsContainer);
console.log('Options count:', optionsContainer ? optionsContainer.children.length : 0);

// 4. Check each option's visibility
if (optionsContainer) {
    console.log('\n--- Individual Options ---');
    Array.from(optionsContainer.children).forEach((opt, i) => {
        const computed = getComputedStyle(opt);
        console.log(`Option ${i}:`, {
            text: opt.textContent,
            display: computed.display,
            visibility: computed.visibility,
            height: computed.height,
            overflow: computed.overflow
        });
    });
}

// 5. Check container dimensions and overflow
console.log('\n--- Container Styles ---');
if (optionsContainer) {
    const computed = getComputedStyle(optionsContainer);
    console.log('Options container:', {
        display: computed.display,
        overflow: computed.overflow,
        overflowY: computed.overflowY,
        maxHeight: computed.maxHeight,
        height: computed.height
    });
}

// 6. Force show all options (test fix)
console.log('\n--- Testing Fix ---');
if (optionsContainer) {
    Array.from(optionsContainer.children).forEach((opt) => {
        opt.style.display = 'block';
        opt.style.visibility = 'visible';
    });
    console.log('âœ… Forced all options to display:block, visibility:visible');
}

return 'Debug complete - check console';

