    }

    # Check for CRITICAL issues first
    $criticalCount = 0
    foreach ($keyword in $priorities.CRITICAL.Keywords) {
        if ($content -match $keyword) {
            $criticalCount++
        }
    }

    if ($criticalCount -gt 0) {
        $results.Critical += @{
