        $relativePath = $file.FullName.Replace($projectRoot, "").TrimStart('\')
        Write-Host "üî¥ CRITICAL: $relativePath ($criticalCount issues)" -ForegroundColor Red
        continue
    }

    # Check for HIGH priority issues
    $highCount = 0
    foreach ($keyword in $priorities.HIGH.Keywords) {
        if ($content -match $keyword) {
            $highCount++
        }
    }

    if ($highCount -gt 0) {
        $results.High += @{
            File = $file
            Count = $highCount
            Priority = "HIGH"
        }
        $relativePath = $file.FullName.Replace($projectRoot, "").TrimStart('\')
        Write-Host "üü° HIGH: $relativePath ($highCount issues)" -ForegroundColor Yellow
