              theme: theme,
              instanceId: instanceId,
              forward: true
            }), '*');
          } catch (frameErr) {
            console.warn('Failed to send theme to child frame', frameErr);
          }
        }
      });
    } catch (err) {
      console.warn('Error broadcasting theme to frames', err);
    }
