// WB Card Web Component
// Website Builder card component with flexible layouts and variants

import { loadComponentCSS } from '../wb-css-loader/wb-css-loader.js';

// Minimal reactive store for state
function createSignal(initial) {
  let value = initial;
  const listeners = [];
  const get = () => value;
  const set = (v) => {
    value = v;
    listeners.forEach(fn => fn(value));
  };
  const subscribe = (fn) => { listeners.push(fn); };
  return [get, set, subscribe];
}

class WBCard extends HTMLElement {
  constructor() {
    super();
    this.attachShadow({ mode: 'open' });

    // Fallback config
    this.config = {
      classes: {
        base: 'wb-card',
        header: 'wb-card-header',
        title: 'wb-card-title',
        subtitle: 'wb-card-subtitle',
        body: 'wb-card-body',
        content: 'wb-card-content',
        media: 'wb-card-media',
        image: 'wb-card-image',
        footer: 'wb-card-footer',
        actions: 'wb-card-actions',
        variants: {
          default: '',
          elevated: 'wb-card--elevated',
          outlined: 'wb-card--outlined',
          filled: 'wb-card--filled',
          glass: 'wb-card--glass'
        },
        sizes: {
          compact: 'wb-card--compact',
          standard: 'wb-card--standard',
          large: 'wb-card--large'
        },
        states: {
          hover: 'wb-card--hover',
          active: 'wb-card--active',
          disabled: 'wb-card--disabled',
          loading: 'wb-card--loading',
          clickable: 'wb-card--clickable'
        },
        layouts: {
          vertical: 'wb-card--vertical',
          horizontal: 'wb-card--horizontal',
          'media-top': 'wb-card--media-top',
          'media-left': 'wb-card--media-left',
          'media-right': 'wb-card--media-right'
        }
      },
      defaults: {
        variant: 'default',
        size: 'standard',
        layout: 'vertical',
        clickable: false,
        loading: false,
        showHeader: true,
        showFooter: false,
        showActions: false
      },
      events: {
        ready: 'wbCardReady',
        click: 'wbCardClick',
        mediaLoad: 'wbCardMediaLoad',
        actionClick: 'wbCardActionClick'
      }
    };

    // Reactive signals
    [this.getTitle, this.setTitle, this.onTitle] = createSignal(this.getAttribute('title') || '');
    [this.getSubtitle, this.setSubtitle, this.onSubtitle] = createSignal(this.getAttribute('subtitle') || '');
    [this.getBody, this.setBody, this.onBody] = createSignal(this.getAttribute('body') || '');
    [this.getVariant, this.setVariant, this.onVariant] = createSignal(this.getAttribute('variant') || this.config.defaults.variant);
    [this.getSize, this.setSize, this.onSize] = createSignal(this.getAttribute('size') || this.config.defaults.size);
    [this.getLayout, this.setLayout, this.onLayout] = createSignal(this.getAttribute('layout') || this.config.defaults.layout);
    [this.getMediaSrc, this.setMediaSrc, this.onMediaSrc] = createSignal(this.getAttribute('media-src') || '');
    [this.getMediaAlt, this.setMediaAlt, this.onMediaAlt] = createSignal(this.getAttribute('media-alt') || '');
    [this.getFooter, this.setFooter, this.onFooter] = createSignal(this.getAttribute('footer') || '');
    [this.getLoading, this.setLoading, this.onLoading] = createSignal(this.hasAttribute('loading'));
    [this.getClickable, this.setClickable, this.onClickable] = createSignal(this.hasAttribute('clickable'));
    [this.getActions, this.setActions, this.onActions] = createSignal(this._parseActions(this.getAttribute('actions')));
    [this.getShowActions, this.setShowActions, this.onShowActions] = createSignal(this.hasAttribute('show-actions'));

    // Subscribe to state changes and re-render
    this.onTitle(() => this.render());
    this.onSubtitle(() => this.render());
    this.onBody(() => this.render());
    this.onVariant(() => this.render());
    this.onSize(() => this.render());
    this.onLayout(() => this.render());
    this.onMediaSrc(() => this.render());
    this.onMediaAlt(() => this.render());
    this.onFooter(() => this.render());
    this.onLoading(() => this.render());
    this.onClickable(() => this.render());
    this.onActions(() => this.render());
    this.onShowActions(() => this.render());
  }

  _parseActions(actionsAttr) {
    if (!actionsAttr) return [];
    try {
      return JSON.parse(actionsAttr);
    } catch (e) {
      return [];
    }
  }

  async connectedCallback() {
    await loadComponentCSS(this, 'wb-card.css');
    this.render();
    this._attachEventListeners();
    this.dispatchEvent(new CustomEvent('wbCardReady', { 
      bubbles: true,
      detail: { component: this, config: this.config }
    }));
  }

  _attachEventListeners() {
    // Card click handler
    this.shadowRoot.addEventListener('click', (e) => {
      // Check if clicked on action button
      const actionBtn = e.target.closest('.wb-card-action-btn');
      if (actionBtn) {
        const actionIndex = parseInt(actionBtn.dataset.actionIndex);
        const actions = this.getActions();
        if (actions[actionIndex]) {
          this.dispatchEvent(new CustomEvent('wbCardActionClick', {
            bubbles: true,
            detail: { action: actions[actionIndex], index: actionIndex, component: this }
          }));
          // Call onClick if provided
          if (typeof actions[actionIndex].onClick === 'function') {
            actions[actionIndex].onClick(e);
          }
        }
        return;
      }

      // Card click (if clickable)
      if (this.getClickable()) {
        this.dispatchEvent(new CustomEvent('wbCardClick', {
          bubbles: true,
          detail: { component: this }
        }));
      }
    });
  }

  render() {
    if (!this.shadowRoot) return;

    // Preserve existing styles
    const existingStyles = this.shadowRoot.querySelectorAll('link, style');
    const stylesHtml = Array.from(existingStyles).map(el => el.outerHTML).join('');

    // Build class list
    const classes = [
      this.config.classes.base,
      this.config.classes.variants[this.getVariant()] || '',
      this.config.classes.sizes[this.getSize()] || '',
      this.config.classes.layouts[this.getLayout()] || '',
      this.getLoading() ? this.config.classes.states.loading : '',
      this.getClickable() ? this.config.classes.states.clickable : ''
    ].filter(Boolean).join(' ');

    // Build actions HTML
    const actions = this.getActions();
    let actionsHtml = '';
    if (this.getShowActions() && actions.length > 0) {
      actionsHtml = `<div class="${this.config.classes.actions}">
        ${actions.map((action, i) => `
          <button class="wb-card-action-btn wb-btn ${action.variant ? 'wb-btn--' + action.variant : ''}" 
                  data-action-index="${i}">
            ${action.text || 'Action'}
          </button>
        `).join('')}
      </div>`;
    }

    // Render the card
    this.shadowRoot.innerHTML = stylesHtml + `
      <div class="${classes}">
        ${this.getMediaSrc() ? `
          <div class="${this.config.classes.media}">
            <img class="${this.config.classes.image}" src="${this.getMediaSrc()}" alt="${this.getMediaAlt()}">
          </div>
        ` : ''}
        ${(this.getTitle() || this.getSubtitle()) ? `
          <div class="${this.config.classes.header}">
            ${this.getTitle() ? `<h3 class="${this.config.classes.title}">${this.getTitle()}</h3>` : ''}
            ${this.getSubtitle() ? `<p class="${this.config.classes.subtitle}">${this.getSubtitle()}</p>` : ''}
          </div>
        ` : ''}
        <div class="${this.config.classes.body}">
          <div class="${this.config.classes.content}">
            ${this.getBody()}
            <slot></slot>
          </div>
        </div>
        ${this.getFooter() ? `<div class="${this.config.classes.footer}">${this.getFooter()}</div>` : ''}
        ${actionsHtml}
      </div>
    `;
  }

  // Public API: setContent for updating card content
  setContent(content) {
    if (content.title !== undefined) this.setTitle(content.title);
    if (content.subtitle !== undefined) this.setSubtitle(content.subtitle);
    if (content.body !== undefined) this.setBody(content.body);
    if (content.footer !== undefined) this.setFooter(content.footer);
    if (content.mediaSrc !== undefined) this.setMediaSrc(content.mediaSrc);
    if (content.mediaAlt !== undefined) this.setMediaAlt(content.mediaAlt);
  }

  // Attribute reflection for reactivity
  static get observedAttributes() {
    return ['title', 'subtitle', 'body', 'variant', 'size', 'layout', 'media-src', 'media-alt', 'footer', 'loading', 'clickable', 'actions', 'show-actions'];
  }

  attributeChangedCallback(name, oldValue, newValue) {
    if (oldValue === newValue) return;

    switch (name) {
      case 'title':
        this.setTitle(newValue || '');
        break;
      case 'subtitle':
        this.setSubtitle(newValue || '');
        break;
      case 'body':
        this.setBody(newValue || '');
        break;
      case 'variant':
        this.setVariant(newValue || this.config.defaults.variant);
        break;
      case 'size':
        this.setSize(newValue || this.config.defaults.size);
        break;
      case 'layout':
        this.setLayout(newValue || this.config.defaults.layout);
        break;
      case 'media-src':
        this.setMediaSrc(newValue || '');
        break;
      case 'media-alt':
        this.setMediaAlt(newValue || '');
        break;
      case 'footer':
        this.setFooter(newValue || '');
        break;
      case 'loading':
        this.setLoading(this.hasAttribute('loading'));
        break;
      case 'clickable':
        this.setClickable(this.hasAttribute('clickable'));
        break;
      case 'actions':
        this.setActions(this._parseActions(newValue));
        break;
      case 'show-actions':
        this.setShowActions(this.hasAttribute('show-actions'));
        break;
    }
  }

  // Static factory method for creating cards programmatically
  static create(content = {}, options = {}) {
    const card = document.createElement('wb-card');

    // Set content attributes
    if (content.title) card.setAttribute('title', content.title);
    if (content.subtitle) card.setAttribute('subtitle', content.subtitle);
    if (content.body) card.setAttribute('body', content.body);
    if (content.footer) card.setAttribute('footer', content.footer);
    if (content.mediaSrc) card.setAttribute('media-src', content.mediaSrc);
    if (content.mediaAlt) card.setAttribute('media-alt', content.mediaAlt);

    // Set options
    if (options.variant) card.setAttribute('variant', options.variant);
    if (options.size) card.setAttribute('size', options.size);
    if (options.layout) card.setAttribute('layout', options.layout);
    if (options.clickable) card.setAttribute('clickable', '');
    if (options.loading) card.setAttribute('loading', '');
    if (options.showActions) card.setAttribute('show-actions', '');

    // Handle actions with onClick functions
    if (content.actions && content.actions.length > 0) {
      // Store onClick functions for later
      card._pendingActions = content.actions;
      card.setAttribute('actions', JSON.stringify(content.actions.map(a => ({ text: a.text, variant: a.variant }))));
      card.setAttribute('show-actions', '');

      // Attach onClick handlers after render
      setTimeout(() => {
        if (card._pendingActions) {
          card.setActions(card._pendingActions);
          delete card._pendingActions;
        }
      }, 0);
    }

    return card;
  }
}

