// Export palette keys for use in demos and integration
export const PALETTE_KEYS = [
  'primary', 'secondary', 'accent', 'highlight',
  'background', 'foreground', 'border',
  'plus30', 'plus45', 'plus60', 'plus90',
  'minus30', 'minus45', 'minus60', 'minus90'
