    constructor() {
        super();
        this.originalValues = {};
        this.initialized = false;
        this.observer = null;
    }

    static get observedAttributes() {
        return ['theme', 'auto-init'];
    }

    connectedCallback() {
        super.connectedCallback(); // Inherit dark mode and other base functionality
        if (!this.initialized) {
            this.init();
        }
    }

    disconnectedCallback() {
        this.cleanup();
    }

    attributeChangedCallback(name, oldValue, newValue) {
        if (!this.initialized) return;

        switch (name) {
            case 'theme':
                if (newValue && newValue !== oldValue) {
                    this.mapThemeColors(newValue);
                }
                break;
            case 'auto-init':
                if (newValue === 'false') {
                    this.cleanup();
                } else if (oldValue === 'false' && newValue !== 'false') {
                    this.init();
                }
                break;
        }
    }

    init() {
        this.logInfo('WB Color Mapper: Initializing...');

        // Hide the component - it's just a controller
        this.style.display = 'none';

        // Initial theme mapping
        const currentTheme = this.getAttribute('theme') || 
                           document.documentElement.getAttribute('data-theme') || 
                           'light';
        this.mapThemeColors(currentTheme);

        // Setup theme change observers
        // Note: WBBaseComponent handles theme changes automatically via _onThemeChange

        this.initialized = true;

        // Dispatch ready event
        this.fireEvent('wb-color-mapper-ready', { component: this });

        this.logInfo('WB Color Mapper: Ready!');
