// Helper function to emit event log events
function logEvent(type, message, details = {}) {
    if (typeof document !== 'undefined' && document.dispatchEvent) {
        document.dispatchEvent(new CustomEvent(`wb:${type}`, {
            detail: {
                message: message,
                source: 'control-panel',
                ...details
            }
        }));
    }
}

