                    <wb-toggle 
                        id="${config.id}"
                        label="${config.label}"
                        ${config.checked ? 'checked' : ''}
                        variant="${config.variant || 'default'}"
                        size="md">
                    </wb-toggle>
                </div>
            `;
        }

        createSliderHTML(config) {
            return `
