    async loadDocumentation() {
        const docContent = this.shadowRoot.getElementById('doc-content');
        let docUrl = this.getAttribute('doc-url');
        // If no doc-url, try markdown attribute
        if (!docUrl) {
            const markdownAttr = this.getAttribute('markdown');
            if (markdownAttr) {
                docUrl = markdownAttr;
            }
