      maxEntries: 50
    }, {
      set: (target, prop, value) => {
        target[prop] = value;
        if (prop === 'log' || prop === 'showLocalLog') {
          this._render();
        }
        return true;
      }
    });
    
    // Bind methods
    this._onError = this._onError.bind(this);
    this._onRejection = this._onRejection.bind(this);
