                ],
                social: [
                    { platform: 'GitHub', url: 'https://github.com/websitebuilder', label: 'Follow us on GitHub' },
                    { platform: 'Twitter', url: 'https://twitter.com/websitebuilder', label: 'Follow us on Twitter' }
                ],
                newsletter: {
                    title: 'Stay Updated',
