            // Load configuration
            await this.loadConfig();

            // Load CSS
            this.loadCSS();

            // Setup component
            this.setupComponent();
            this.registerDefaultShortcuts();
            this.setupEventListeners();

            this.isInitialized = true;
            if (window.WBEventLog) {
