                    shortcuts: Array.from(this.shortcuts.keys()) 
                });
            } else {
                console.log('‚å®Ô∏è Registered shortcuts:', Array.from(this.shortcuts.keys()));
            }
        }

        setupEventListeners() {
            // Single document-level keydown listener
            document.addEventListener('keydown', this.handleKeyDown.bind(this));

            // Context change listeners
            document.addEventListener('focusin', this.handleFocusChange.bind(this));
            document.addEventListener('focusout', this.handleFocusChange.bind(this));

            // Component lifecycle listeners
            document.addEventListener('wb:component-focused', this.handleComponentFocus.bind(this));
            document.addEventListener('wb:edit-mode-changed', this.handleEditModeChange.bind(this));
        }

        handleKeyDown(e) {
            if (!this.enabled) return;

            // Prevent duplicate event processing
            if (e._wbKeyboardHandled) return;
            e._wbKeyboardHandled = true;

            // Build key combination string
            const key = this.buildKeyString(e);

            if (window.WBEventLog) {
                WBEventLog.logDebug('Key pressed', { 
                    component: 'wb-keyboard-manager', 
                    method: 'handleKeyDown', 
                    line: 215, 
                    key: key, 
                    sequence: this.keySequence 
                });
            } else {
                console.log('‚å®Ô∏è Key pressed:', key, 'Current sequence:', this.keySequence);
            }

            // Handle multi-key sequences (like Ctrl+K+H)
            if (this.isSequenceStart(key)) {
                if (window.WBEventLog) {
                    WBEventLog.logDebug('Starting sequence', { 
                        component: 'wb-keyboard-manager', 
                        method: 'handleKeyDown', 
                        line: 219, 
                        key: key 
                    });
                } else {
                    console.log('‚å®Ô∏è Starting sequence with:', key);
                }
                e.preventDefault();
                this.keySequence = [key];

                // Clear sequence after timeout
                setTimeout(() => {
                    if (this.keySequence.length > 0) {
                        if (window.WBEventLog) {
                            WBEventLog.logDebug('Sequence timeout, clearing', { 
                                component: 'wb-keyboard-manager', 
                                method: 'sequenceTimeoutHandler', 
                                line: 226, 
                                sequence: this.keySequence 
                            });
                        } else {
                            console.log('‚å®Ô∏è Sequence timeout, clearing:', this.keySequence);
                        }
                        this.keySequence = [];
                    }
                }, this.sequenceTimeout);

                return;
            }

            // Build full key combination
            let fullKey = key;
            if (this.keySequence.length > 0) {
                fullKey = [...this.keySequence, key].join('+');
                if (window.WBEventLog) {
                    WBEventLog.logDebug('Built sequence', { 
                        component: 'wb-keyboard-manager', 
                        method: 'handleKeyDown', 
                        line: 238, 
                        fullKey: fullKey 
                    });
                } else {
                    console.log('‚å®Ô∏è Built sequence:', fullKey);
                }
                this.keySequence = []; // Clear sequence
            }

            // Find matching shortcut
            const shortcut = this.findShortcut(fullKey);
            if (window.WBEventLog) {
                WBEventLog.logDebug('Looking for shortcut', { 
                    component: 'wb-keyboard-manager', 
                    method: 'handleKeyDown', 
                    line: 244, 
                    fullKey: fullKey, 
                    found: !!shortcut 
                });
            } else {
                console.log('‚å®Ô∏è Looking for shortcut:', fullKey, 'Found:', !!shortcut);
            }

            if (shortcut && this.isShortcutActive(shortcut)) {
                if (window.WBEventLog) {
                    WBEventLog.logUser('Executing shortcut', { 
                        component: 'wb-keyboard-manager', 
                        method: 'handleKeyDown', 
                        line: 247, 
                        action: shortcut.action, 
                        keys: shortcut.keys 
                    });
                } else {
                    console.log('‚å®Ô∏è Executing shortcut:', shortcut.action);
                }

                // Dispatch before event
                const beforeEvent = new CustomEvent('wb:keyboard-shortcut-before', {
                    detail: { shortcut, event: e },
                    cancelable: true,
                    bubbles: true
                });

                if (!document.dispatchEvent(beforeEvent)) {
                    return; // Event was cancelled
                }

                // Prevent default and stop propagation if specified
                if (shortcut.preventDefault !== false) {
                    e.preventDefault();
                }
                if (shortcut.stopPropagation !== false) {
                    e.stopPropagation();
                    e.stopImmediatePropagation(); // Also prevent other handlers on same element
                }

                // Execute shortcut handler
                try {
                    if (shortcut.handler) {
                        shortcut.handler(e);
                    }

                    // Dispatch after event
                    document.dispatchEvent(new CustomEvent('wb:keyboard-shortcut-executed', {
                        detail: { shortcut, event: e },
                        bubbles: true
                    }));

                    // Log the action
                    this.logShortcutExecution(shortcut);

                } catch (error) {
                    if (window.WBEventLog) {
                        WBEventLog.logError('Error executing keyboard shortcut', { 
                            component: 'wb-keyboard-manager', 
                            method: 'handleKeyDown', 
                            line: 285, 
                            error: error.message, 
                            stack: error.stack 
                        });
                    } else {
                        console.error('‚å®Ô∏è Error executing keyboard shortcut:', error);
                    }

                    // Dispatch error event
                    document.dispatchEvent(new CustomEvent('wb:keyboard-shortcut-error', {
                        detail: { shortcut, error, event: e },
                        bubbles: true
                    }));
                }
            }
        }

        buildKeyString(e) {
            const parts = [];

            // Add modifiers in consistent order
            if (e.ctrlKey || e.metaKey) parts.push('ctrl');
            if (e.altKey) parts.push('alt');
            if (e.shiftKey && e.key !== '?' && e.key !== 'Shift') parts.push('shift'); // Don't add shift for ? key

            // Add main key
            let key = e.key.toLowerCase();

            // Handle special characters that might have different key vs code values
            if (e.key === '?') {
                key = '?';
            }

            // Map special keys
            const keyMap = {
                ' ': 'space',
                'arrowup': 'up',
                'arrowdown': 'down',
                'arrowleft': 'left',
                'arrowright': 'right',
                'escape': 'escape'
            };

            parts.push(keyMap[key] || key);

            return parts.join('+');
        }

        isSequenceStart(key) {
            // Check if this key starts a multi-key sequence
            for (const [shortcutKey] of this.shortcuts) {
                const parts = shortcutKey.split('+');
                // Check if this is a multi-key sequence (more than 2 parts) and starts with current key
                if (parts.length > 2 && shortcutKey.startsWith(key + '+')) {
                    return true;
                }
            }
            return false;
        }

        findShortcut(key) {
            return this.shortcuts.get(key);
        }

        isShortcutActive(shortcut) {
            // Check if shortcut is enabled
            if (shortcut.enabled === false) return false;

            // Check context
            if (shortcut.context && shortcut.context !== 'global') {
                return this.activeContext === shortcut.context || 
                       this.contexts.has(shortcut.context);
            }

            // For global shortcuts, check if there's a more specific context shortcut
            if (shortcut.context === 'global' && this.hasSpecificContextShortcut(shortcut.keys)) {
                // A component-specific shortcut takes priority over global
                return this.activeContext === 'global';
            }

            return true;
        }

        hasSpecificContextShortcut(keys) {
            // Check if there's a non-global shortcut with the same keys
            for (const [shortcutKeys, shortcut] of this.shortcuts) {
                if (shortcutKeys === keys && shortcut.context !== 'global' && 
                    (this.activeContext === shortcut.context || this.contexts.has(shortcut.context))) {
                    return true;
                }
            }
            return false;
        }

        handleFocusChange(e) {
            // Update active context based on focused element
            const focusedElement = document.activeElement;

            if (focusedElement && focusedElement.tagName) {
                const tagName = focusedElement.tagName.toLowerCase();

                // Check for WB component
                if (tagName.startsWith('wb-')) {
                    this.setActiveContext(tagName);
                } else if (focusedElement.closest('[data-component]')) {
                    const component = focusedElement.closest('[data-component]').getAttribute('data-component');
                    this.setActiveContext(component);
                } else {
                    this.setActiveContext('global');
                }
            }
        }

        handleComponentFocus(e) {
            if (e.detail && e.detail.component) {
                this.setActiveContext(e.detail.component);
            }
        }

        handleEditModeChange(e) {
            if (e.detail && e.detail.enabled) {
                this.contexts.add('edit-mode');
            } else {
                this.contexts.delete('edit-mode');
            }
        }

        setActiveContext(context) {
            this.activeContext = context;
            this.contexts.add(context);

            // Dispatch context change event
            document.dispatchEvent(new CustomEvent('wb:keyboard-context-changed', {
                detail: { context, activeContext: this.activeContext },
                bubbles: true
            }));
        }

        isInputFocused() {
            const activeElement = document.activeElement;
            if (!activeElement) return false;

            const tagName = activeElement.tagName.toLowerCase();
            return tagName === 'input' || 
                   tagName === 'textarea' || 
                   tagName === 'select' ||
                   activeElement.contentEditable === 'true';
        }

        logShortcutExecution(shortcut) {
            // Dispatch event for logging
            document.dispatchEvent(new CustomEvent('wb:user', {
                detail: {
                    message: `Keyboard shortcut executed: ${shortcut.description}`,
                    source: 'wb-keyboard-manager',
                    action: shortcut.action,
                    keys: shortcut.keys,
                    context: shortcut.context
                }
            }));
        }

        // Public API Methods
        registerShortcut(shortcut) {
            if (!shortcut.keys || !shortcut.action) {
                if (window.WBEventLog) {
                    WBEventLog.logWarning('Invalid shortcut registration', { 
                        component: 'wb-keyboard-manager', 
                        method: 'registerShortcut', 
                        line: 444, 
                        shortcut: shortcut 
                    });
                } else {
                    console.warn('‚å®Ô∏è Invalid shortcut registration:', shortcut);
                }
                return false;
            }

            // Normalize keys
            const normalizedKeys = this.normalizeKeys(shortcut.keys);

            // Check for conflicts
            if (this.shortcuts.has(normalizedKeys)) {
                const existing = this.shortcuts.get(normalizedKeys);

                // Only warn if it's actually a different shortcut
                if (existing.action !== shortcut.action) {
                    if (window.WBEventLog) {
                        WBEventLog.logWarning('Keyboard shortcut conflict detected', { 
                            component: 'wb-keyboard-manager', 
                            method: 'registerShortcut', 
                            line: 457, 
                            keys: normalizedKeys,
                            existing: { action: existing.action, context: existing.context },
                            new: { action: shortcut.action, context: shortcut.context }
                        });
                    } else {
                        console.warn('‚å®Ô∏è Keyboard shortcut conflict detected:', {
                            keys: normalizedKeys,
                            existing: { action: existing.action, context: existing.context },
                            new: { action: shortcut.action, context: shortcut.context }
                        });
                    }

                    // Dispatch conflict event
                    document.dispatchEvent(new CustomEvent('wb:keyboard-conflict', {
                        detail: { 
                            keys: normalizedKeys,
                            existing: existing,
                            new: shortcut
                        },
                        bubbles: true
                    }));
                } else {
                    // Same shortcut being re-registered, just update it silently
                    if (window.WBEventLog) {
                        WBEventLog.logInfo('Re-registering existing shortcut', { 
                            component: 'wb-keyboard-manager', 
                            method: 'registerShortcut', 
                            line: 474, 
                            keys: normalizedKeys 
                        });
                    } else {
                        console.log('‚å®Ô∏è Re-registering existing shortcut:', normalizedKeys);
                    }
                }
            }

            // Set defaults
            const fullShortcut = {
                enabled: true,
                preventDefault: true,
                stopPropagation: true,
                context: 'global',
                ...shortcut,
                keys: normalizedKeys
            };

            this.shortcuts.set(normalizedKeys, fullShortcut);

            if (window.WBEventLog) {
                WBEventLog.logSuccess('Registered shortcut', { 
                    component: 'wb-keyboard-manager', 
                    method: 'registerShortcut', 
                    line: 490, 
                    shortcut: fullShortcut 
                });
            } else {
                console.log('‚å®Ô∏è Registered shortcut:', fullShortcut);
            }
            return true;
        }

        unregisterShortcut(keys) {
            const normalizedKeys = this.normalizeKeys(keys);
            const removed = this.shortcuts.delete(normalizedKeys);

            if (removed) {
                if (window.WBEventLog) {
                    WBEventLog.logInfo('Unregistered shortcut', { 
                        component: 'wb-keyboard-manager', 
                        method: 'unregisterShortcut', 
                        line: 499, 
                        keys: normalizedKeys 
                    });
                } else {
                    console.log('‚å®Ô∏è Unregistered shortcut:', normalizedKeys);
                }
            }

            return removed;
        }

        getShortcuts(context = null) {
            if (!context) {
                return Array.from(this.shortcuts.values());
            }

            return Array.from(this.shortcuts.values()).filter(s => s.context === context);
        }

        normalizeKeys(keys) {
            // Normalize key combination string
            return keys.toLowerCase()
                      .replace(/\s+/g, '')
                      .replace(/control/g, 'ctrl')
                      .replace(/option/g, 'alt')
                      .replace(/cmd|super|meta/g, 'ctrl');
        }

        enable() {
            this.enabled = true;
            if (window.WBEventLog) {
                WBEventLog.logSuccess('Keyboard manager enabled', { 
                    component: 'wb-keyboard-manager', 
                    method: 'enable', 
                    line: 524 
                });
            } else {
                console.log('‚å®Ô∏è Keyboard manager enabled');
            }
        }

        disable() {
            this.enabled = false;
            if (window.WBEventLog) {
                WBEventLog.logInfo('Keyboard manager disabled', { 
                    component: 'wb-keyboard-manager', 
                    method: 'disable', 
                    line: 529 
                });
            } else {
                console.log('‚å®Ô∏è Keyboard manager disabled');
            }
        }

        showHelp() {
            this.helpModalVisible = true;
            this.renderHelpModal();

            // Dispatch event
            document.dispatchEvent(new CustomEvent('wb:keyboard-help-shown', {
                bubbles: true
            }));
        }

        hideHelp() {
            this.helpModalVisible = false;
            const modal = document.getElementById('wb-keyboard-help-modal');
            if (modal) {
                modal.remove();
            }

            // Dispatch event
            document.dispatchEvent(new CustomEvent('wb:keyboard-help-hidden', {
                bubbles: true
            }));
        }

        renderHelpModal() {
            // Remove existing modal
            const existingModal = document.getElementById('wb-keyboard-help-modal');
            if (existingModal) {
                existingModal.remove();
            }

            // Group shortcuts by context
            const shortcutsByContext = this.groupShortcutsByContext();

            // Create modal
            const modal = document.createElement('div');
            modal.id = 'wb-keyboard-help-modal';
            modal.className = 'wb-keyboard-help-modal';
            modal.innerHTML = `
                <div class="wb-keyboard-help-overlay" onclick="document.querySelector('wb-keyboard-manager').hideHelp()"></div>
                <div class="wb-keyboard-help-content">
                    <div class="wb-keyboard-help-header">
                        <h2>‚å®Ô∏è Keyboard Shortcuts</h2>
                        <button class="wb-keyboard-help-close" onclick="document.querySelector('wb-keyboard-manager').hideHelp()">‚úï</button>
                    </div>
                    <div class="wb-keyboard-help-body">
                        ${this.renderShortcutGroups(shortcutsByContext)}
                    </div>
                    <div class="wb-keyboard-help-footer">
                        <p>Press <kbd>Escape</kbd> to close this help</p>
                    </div>
                </div>
            `;

            document.body.appendChild(modal);

            // Focus the modal for accessibility
            modal.querySelector('.wb-keyboard-help-content').focus();
        }

        groupShortcutsByContext() {
            const groups = {};

            for (const shortcut of this.shortcuts.values()) {
                const context = shortcut.context || 'global';
                if (!groups[context]) {
                    groups[context] = [];
                }
                groups[context].push(shortcut);
            }

            return groups;
        }

        renderShortcutGroups(groups) {
            return Object.entries(groups).map(([context, shortcuts]) => {
                const contextName = context === 'global' ? 'Global' : context;

                return `
                    <div class="wb-keyboard-help-group">
                        <h3>${contextName}</h3>
                        <div class="wb-keyboard-help-shortcuts">
                            ${shortcuts.map(shortcut => `
                                <div class="wb-keyboard-help-shortcut">
                                    <div class="wb-keyboard-help-keys">
                                        ${this.renderKeys(shortcut.keys)}
                                    </div>
                                    <div class="wb-keyboard-help-description">
                                        ${shortcut.description}
                                    </div>
                                </div>
                            `).join('')}
                        </div>
                    </div>
                `;
            }).join('');
        }

        renderKeys(keys) {
            return keys.split('+').map(key => {
                // Capitalize and format keys
                const formatted = key === 'ctrl' ? 'Ctrl' :
                                 key === 'alt' ? 'Alt' :
                                 key === 'shift' ? 'Shift' :
                                 key === 'space' ? 'Space' :
                                 key.length === 1 ? key.toUpperCase() :
                                 key.charAt(0).toUpperCase() + key.slice(1);

                return `<kbd>${formatted}</kbd>`;
            }).join(' + ');
        }
    }

    // Register the custom element
    if (!customElements.get('wb-keyboard-manager')) {
        customElements.define('wb-keyboard-manager', WBKeyboardManager);
        if (window.WBEventLog) {
            WBEventLog.logSuccess('WB Keyboard Manager: Component registered', { 
                component: 'wb-keyboard-manager', 
                method: 'componentRegistration', 
                line: 647 
            });
        } else {
            console.log('‚å®Ô∏è WB Keyboard Manager: Component registered');
        }
    }

    // Register with WBComponentRegistry if available
    if (window.WBComponentRegistry && typeof window.WBComponentRegistry.register === 'function') {
        window.WBComponentRegistry.register('wb-keyboard-manager', WBKeyboardManager, ['wb-event-log'], {
            version: '1.0.0',
            type: 'input',
            role: 'infrastructure',
            description: 'Global keyboard shortcut management system for WB components',
            api: {
                static: ['register', 'unregister', 'trigger', 'disable', 'enable'],
                events: ['keydown', 'keyup', 'shortcut-triggered'],
                attributes: ['data-shortcuts', 'data-global', 'data-scope'],
                methods: ['registerShortcut', 'unregisterShortcut', 'getShortcuts', 'formatShortcut']
            },
            priority: 2 // High priority infrastructure component, depends on wb-event-log
        });
    }

    // Make class globally available
    window.WBKeyboardManager = WBKeyboardManager;

})();

/**
 * WB Layout Component - Reactive Architecture
 * Handles website layout switching with internal state management
 * 
 * Supported layouts:
 * - top-nav: Horizontal navigation at page top
 * - left-nav: Fixed left sidebar navigation
 * - right-nav: Fixed right sidebar navigation  
 * - ad-layout: Advertisement optimized with enhanced navigation
 */

(function() {
    'use strict';

    class WBLayout extends HTMLElement {
        constructor() {
            super();
            
            // Cache DOM elements during initialization
            this._elements = {};
            
            // Layout configuration (internal data)
            this._layoutConfigs = {
                'top-nav': {
                    name: 'Top Navigation',
                    description: 'Horizontal navigation bar at page top',
                    bodyClass: 'layout-top-nav',
                    navComponent: 'wb-nav',
                    navConfig: { type: 'horizontal', position: 'top' }
                },
                'left-nav': {
                    name: 'Left Sidebar',
                    description: 'Fixed vertical sidebar with icons and text',
                    bodyClass: 'layout-left-nav',
                    navComponent: 'wb-nav',
                    navConfig: { type: 'vertical', position: 'left', width: '200px' }
                },
                'right-nav': {
                    name: 'Right Sidebar', 
                    description: 'Fixed vertical sidebar with icons and text',
                    bodyClass: 'layout-right-nav',
                    navComponent: 'wb-nav',
                    navConfig: { type: 'vertical', position: 'right', width: '200px' }
                },
                'ad-layout': {
                    name: 'Ad Layout',
                    description: 'Advertisement optimized with branding and CTA',
                    bodyClass: 'layout-ad-layout',
                    navComponent: 'wb-nav',
                    navConfig: { type: 'enhanced', branding: true, cta: true }
                }
            };
            
            // Reactive state using Proxy for automatic UI updates
            this._state = new Proxy({
                currentLayout: 'top-nav',
                showControls: false,
                responsiveMode: 'desktop',
                autoApply: true
            }, {
                set: (target, property, value) => {
                    if (target[property] !== value) {
                        const oldValue = target[property];
                        target[property] = value;
                        this._handleStateChange(property, value, oldValue);
                    }
                    return true;
                }
            });
        }

        connectedCallback() {
            this._initializeComponent();
        }

        static get observedAttributes() {
            return ['layout', 'auto-apply', 'show-controls'];
        }

        attributeChangedCallback(name, oldValue, newValue) {
            if (!this._state) return; // Not initialized yet
            
            switch (name) {
                case 'layout':
                    if (newValue && newValue !== oldValue) {
                        this._state.currentLayout = newValue;
                    }
                    break;
                case 'auto-apply':
                    this._state.autoApply = newValue !== 'false';
                    break;
                case 'show-controls':
                    this._state.showControls = newValue === 'true';
                    break;
            }
        }

        // REACTIVE STATE MANAGEMENT
        _handleStateChange(property, newValue, oldValue) {
            switch (property) {
                case 'currentLayout':
                    this._updateLayout(newValue, oldValue);
                    break;
                case 'showControls':
                    this._updateControlsVisibility();
                    break;
                case 'responsiveMode':
                    this._updateResponsiveMode();
                    break;
                case 'autoApply':
                    // No immediate action needed
                    break;
            }
            
            // Always emit change events for external notification
            this._emitStateChange(property, newValue, oldValue);
        }

        _updateLayout(newLayout, oldLayout) {
            const config = this._layoutConfigs[newLayout];
            if (!config) {
                console.warn(`üé® WB Layout: Unknown layout type '${newLayout}'`);
                return;
            }

            // Update all UI elements automatically
            this._updatePageLayout(config, oldLayout);
            this._updateNavigationComponent(config);
            this._updateControlSelect();
            this._updateComponentAttributes();

            console.log(`üé® WB Layout: Reactive change from '${oldLayout}' to '${newLayout}'`);
        }

        _updateControlsVisibility() {
            if (this._elements.control) {
                this._elements.control.style.display = this._state.showControls ? 'block' : 'none';
            }
        }

        _updateResponsiveMode() {
            // Handle responsive mode changes if needed
            document.documentElement.setAttribute('data-responsive-mode', this._state.responsiveMode);
        }

        // INITIALIZATION
        _initializeComponent() {
            // Get initial values from attributes
            const initialLayout = this.getAttribute('layout') || 'top-nav';
            const autoApply = this.getAttribute('auto-apply') !== 'false';
            const showControls = this.getAttribute('show-controls') === 'true';
            
            // Store existing content
            const existingContent = this.innerHTML;
            
            // Add control template if not exists
            if (!this.querySelector('.wb-layout-control')) {
                this.insertAdjacentHTML('afterbegin', this._getControlTemplate());
            }
            
            // Cache DOM elements
            this._cacheElements();
            
            // Setup internal event handlers
            this._setupInternalEventHandlers();
            
            // Initialize reactive state (triggers automatic updates)
            this._state.currentLayout = initialLayout;
            this._state.autoApply = autoApply;
            this._state.showControls = showControls;

            // Detect responsive mode
            this._detectResponsiveMode();

            console.log('üé® WB Layout component initialized with reactive architecture');
            this.dispatchEvent(new CustomEvent('wb-layout-ready', {
                detail: { component: this, layout: this._state.currentLayout }
            }));
        }

        _cacheElements() {
            // Cache all DOM elements once during initialization
            this._elements = {
                control: this.querySelector('.wb-layout-control'),
                select: this.querySelector('#wb-layout-select'),
                body: document.body,
                documentElement: document.documentElement,
                nav: null // Will be found dynamically
            };
        }

        _getControlTemplate() {
            return `
                <div class="wb-layout-control" style="display: none;">
                    <label for="wb-layout-select">Layout:</label>
                    <select id="wb-layout-select">
                        ${Object.entries(this._layoutConfigs).map(([key, config]) => 
                            `<option value="${key}">${config.name}</option>`
                        ).join('')}
                    </select>
                </div>
            `;
        }

        _setupInternalEventHandlers() {
            // Internal control handling
            if (this._elements.select) {
                this._elements.select.addEventListener('change', (e) => {
                    this._state.currentLayout = e.target.value; // Triggers reactive update
                });
            }

            // Listen for external layout change requests (for backward compatibility)
            document.addEventListener('wb-layout-change', (e) => {
                if (e.detail.layout && e.detail.layout !== this._state.currentLayout) {
                    this._state.currentLayout = e.detail.layout; // Triggers reactive update
                }
            });

            // Responsive breakpoint detection
            if (window.matchMedia) {
                const mobileQuery = window.matchMedia('(max-width: 768px)');
                const tabletQuery = window.matchMedia('(max-width: 1024px)');
                
                const updateResponsiveMode = () => {
                    if (mobileQuery.matches) {
                        this._state.responsiveMode = 'mobile';
                    } else if (tabletQuery.matches) {
                        this._state.responsiveMode = 'tablet';
                    } else {
                        this._state.responsiveMode = 'desktop';
                    }
                };
                
                mobileQuery.addListener(updateResponsiveMode);
                tabletQuery.addListener(updateResponsiveMode);
                updateResponsiveMode(); // Initial check
            }
        }

        // UI UPDATE METHODS (Called automatically by reactive state)
        _updatePageLayout(config, oldLayout) {
            const body = this._elements.body;
            
            // Remove old layout class
            if (oldLayout && this._layoutConfigs[oldLayout]) {
                body.classList.remove(this._layoutConfigs[oldLayout].bodyClass);
            }

            // Add new layout class
            body.classList.add(config.bodyClass);

            // Update data attributes
            body.setAttribute('data-layout', this._state.currentLayout);
            
            // Apply layout-specific CSS custom properties
            this._applyLayoutStyles(config);
            
            // Apply layout to component structure
            this._applyComponentStructure(config);
        }

        _applyLayoutStyles(config) {
            const root = this._elements.documentElement;
            
            switch (this._state.currentLayout) {
                case 'top-nav':
                    root.style.setProperty('--nav-width', '100%');
                    root.style.setProperty('--nav-height', '60px');
                    root.style.setProperty('--content-margin-left', '0');
                    root.style.setProperty('--content-margin-top', '60px');
                    break;
                    
                case 'left-nav':
                    root.style.setProperty('--nav-width', '200px');
                    root.style.setProperty('--nav-height', '100vh');
                    root.style.setProperty('--content-margin-left', '200px');
                    root.style.setProperty('--content-margin-top', '0');
                    break;
                    
                case 'right-nav':
                    root.style.setProperty('--nav-width', '200px');
                    root.style.setProperty('--nav-height', '100vh');
                    root.style.setProperty('--content-margin-left', '0');
                    root.style.setProperty('--content-margin-right', '200px');
                    root.style.setProperty('--content-margin-top', '0');
                    break;
                    
                case 'ad-layout':
                    root.style.setProperty('--nav-width', '100%');
                    root.style.setProperty('--nav-height', '80px');
                    root.style.setProperty('--content-margin-left', '0');
                    root.style.setProperty('--content-margin-top', '80px');
                    break;
            }
        }

        _applyComponentStructure(config) {
            // Apply CSS classes to organize child elements based on layout
            this.className = `wb-layout wb-layout-${this._state.currentLayout}`;
            
            // Ensure the component displays as flex with proper layout
            this.style.display = 'flex';
            
            switch (this._state.currentLayout) {
                case 'top-nav':
                    this.style.flexDirection = 'column';
                    this._organizeTopNavLayout();
                    break;
                case 'left-nav':
                    this.style.flexDirection = 'row';
                    this._organizeLeftNavLayout();
                    break;
                case 'right-nav':
                    this.style.flexDirection = 'row-reverse';
                    this._organizeRightNavLayout();
                    break;
                case 'ad-layout':
                    this.style.flexDirection = 'column';
                    this._organizeAdLayout();
                    break;
            }
        }

        _organizeTopNavLayout() {
            this._removeContentColumn();
        }
        
        _organizeLeftNavLayout() {
            this._createContentColumn();
        }
        
        _organizeRightNavLayout() {
            this._createContentColumn();
        }
        
        _organizeAdLayout() {
            this._removeContentColumn();
        }
        
        _createContentColumn() {
            // Find all non-nav elements
            const nav = this.querySelector('wb-nav');
            const header = this.querySelector('header');
            const main = this.querySelector('main');
            const footer = this.querySelector('footer');
            const aside = this.querySelector('aside');
            
            // Remove existing content column if it exists
            const existingColumn = this.querySelector('.wb-content-column');
            if (existingColumn) {
                while (existingColumn.firstChild) {
                    this.appendChild(existingColumn.firstChild);
                }
                existingColumn.remove();
            }
            
            // Create content column container
            const contentColumn = document.createElement('div');
            contentColumn.className = 'wb-content-column';
            contentColumn.style.cssText = 'display: flex; flex-direction: column; flex: 1; min-height: 100vh; width: 100%;';
            
            // Move elements into content column
            [header, main, footer].forEach(element => {
                if (element) {
                    contentColumn.appendChild(element);
                }
            });
            
            // Insert content column appropriately
            if (nav) {
                this.insertBefore(contentColumn, nav.nextSibling);
            } else {
                this.appendChild(contentColumn);
            }
            
            // Add aside if it exists
            if (aside && !contentColumn.contains(aside)) {
                this.appendChild(aside);
            }
