  _handleWbEvent(event) {
    if (event.detail && event.detail.message) {
      const timestamp = new Date().toLocaleTimeString();
      const type = event.type.replace('wb:', '').toUpperCase();
      const entry = `[${timestamp}] ${type}: ${event.detail.message}`;
      this.add(entry);
