  add(entry) {
    const newLog = [entry, ...this._state.log];
    if (newLog.length > this._state.maxEntries) {
      newLog.length = this._state.maxEntries;
    }
    this._state.log = newLog;
  }

  clear() {
    this._state.log = [];
