            return Promise.reject(new Error('Invalid modal or modal.hide method not found'));
        },

        // Alert modal
        alert: function(message, title = 'Alert', triggerElement = null) {
            const modal = this.create({
                title: title,
                content: `<p>${message}</p>`,
                buttons: [{
                    text: 'OK',
                    variant: 'primary',
                    onclick: () => this.hide(modal)
                }]
            });
            this.show(modal, triggerElement);
            return modal;
        },

        // Confirm modal
        confirm: function(message, title = 'Confirm', triggerElement = null) {
            return new Promise((resolve) => {
                const modal = this.create({
                    title: title,
                    content: `<p>${message}</p>`,
                    buttons: [{
                        text: 'Cancel',
                        variant: 'secondary',
                        onclick: () => {
                            this.hide(modal);
                            resolve(false);
                        }
                    }, {
                        text: 'Confirm',
                        variant: 'primary',
                        onclick: () => {
                            this.hide(modal);
                            resolve(true);
                        }
                    }]
                });
                this.show(modal, triggerElement);
            });
        },

        // Prompt modal
        prompt: function(message, defaultValue = '', title = 'Input', triggerElement = null) {
            return new Promise((resolve) => {
                const inputId = 'prompt-input-' + Date.now();
                const modal = this.create({
                    title: title,
                    content: `
                        <p>${message}</p>
                        <input type="text" id="${inputId}" class="wb-input-field" value="${defaultValue}" style="width: 100%; margin-top: 1rem;">
                    `,
                    buttons: [{
                        text: 'Cancel',
                        variant: 'secondary',
                        onclick: () => {
                            this.hide(modal);
                            resolve(null);
                        }
                    }, {
                        text: 'OK',
                        variant: 'primary',
                        onclick: () => {
                            const input = modal.querySelector(`#${inputId}`);
                            const value = input ? input.value : '';
                            this.hide(modal);
                            resolve(value);
                        }
                    }]
                });
                this.show(modal, triggerElement);

                // Focus the input after modal opens
                setTimeout(() => {
                    const input = modal.querySelector(`#${inputId}`);
                    if (input) {
                        input.focus();
                        input.select();
                    }
                }, 100);
            });
        },

        // Close all modals
        closeAll: function() {
            return new Promise((resolve) => {
                const modals = document.querySelectorAll('wb-modal');
                const backdrops = document.querySelectorAll('.wb-modal-backdrop');

                // Hide all modals
                modals.forEach(modal => {
                    if (typeof modal.hide === 'function') {
                        modal.hide();
                    }
                });

                // Remove any remaining backdrops
                backdrops.forEach(backdrop => {
                    backdrop.remove();
                });

                // Wait for animations to complete
                setTimeout(() => {
                    resolve();
                }, 600);
            });
        }
    };

    // Auto-initialize when DOM is ready
    function initialize() {
        console.log('ü™ü WB Modal: Component initialized successfully');

        // Dispatch ready event
        const event = new CustomEvent('wbModalReady', {
            detail: { component: 'wb-modal' }
        });
