                    }
                }
            }
        });
        
        // Keyboard navigation
        this.addEventListener('keydown', (e) => {
            this.handleKeyboardNavigation(e);
        });
        
        // Close mobile menu when clicking outside
        document.addEventListener('click', (e) => {
            const t = e.target;
            if (this.isExpanded && t instanceof Node && !this.contains(t)) {
