// WB Resize Component
// Provides drag and resize functionality for any container element

// Use WBBaseComponent from window if available, otherwise extend HTMLElement
const BaseClass = window.WBBaseComponent || HTMLElement;

class WBResizePanel extends BaseClass {
    constructor() {
        super();

        console.log('üîß WB Resize: Starting initialization...');

        this.isDragging = false;
        this.isResizing = false;
        this.startX = 0;
        this.startY = 0;
        this.initialX = 0;
        this.initialY = 0;
        this.initialWidth = 0;
        this.initialHeight = 0;
        this.targetElement = null;
    }

    connectedCallback() {
        this.render();
        this.setupEventListeners();
        this.findTarget();
    }

    disconnectedCallback() {
        // Clean up global event listeners
        document.removeEventListener('mousemove', this.boundOnMouseMove);
        document.removeEventListener('mouseup', this.boundOnMouseUp);
    }

    render() {
        this.innerHTML = `
            <div class="wb-resize-controls">
                <button
                    class="wb-resize-drag-handle"
                    id="drag-handle"
                    title="Drag to move"
                    aria-label="Drag to move element">
                    <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <circle cx="10" cy="10" r="8" stroke="currentColor" stroke-width="2"/>
                        <path d="M10 6V14M6 10H14" stroke="currentColor" stroke-width="2" stroke-linecap="round"/>
                    </svg>
                </button>

                <button
                    class="wb-resize-resize-handle"
                    id="resize-handle"
                    title="Drag to resize width and height"
                    aria-label="Resize element">
                    <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <path d="M14 6L18 10L14 14M6 14L2 10L6 6M10 2L10 18" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
                    </svg>
                </button>
            </div>
        `;
    }

    findTarget() {
        // Find the parent element that should be made draggable/resizable
        const targetSelector = this.getAttribute('target');

        if (targetSelector) {
            this.targetElement = document.querySelector(targetSelector);
        } else {
            // Default to parent element
            this.targetElement = this.parentElement;
        }

        if (!this.targetElement) {
            console.error('üîß WB Resize: No target element found');
            console.warn('Make sure element has id or target attribute is set');
            return;
        }

        // Ensure target has position set
        if (this.targetElement instanceof HTMLElement) {
            if (!this.targetElement.style.position || this.targetElement.style.position === 'static') {
                this.targetElement.style.position = 'fixed';
            }
        }

        console.log('üîß WB Resize: Target element found:', this.targetElement);
    }

    setupEventListeners() {
        const dragHandle = this.querySelector('#drag-handle');
        const resizeHandle = this.querySelector('#resize-handle');

        // Drag functionality
        if (dragHandle) dragHandle.addEventListener('mousedown', (e) => this.startDrag(e));

        // Resize functionality
        if (resizeHandle) resizeHandle.addEventListener('mousedown', (e) => this.startResize(e));

        // Bind methods to preserve 'this' context
        this.boundOnMouseMove = (e) => this.onMouseMove(e);
        this.boundOnMouseUp = () => this.onMouseUp();

        // Global mouse move and up
        document.addEventListener('mousemove', this.boundOnMouseMove);
        document.addEventListener('mouseup', this.boundOnMouseUp);

        // Hover effects
        if (dragHandle) {
            dragHandle.addEventListener('mouseenter', () => {
                dragHandle.style.transform = 'scale(1.1)';
                dragHandle.style.opacity = '1';
            });
            dragHandle.addEventListener('mouseleave', () => {
                if (!this.isDragging) {
                    dragHandle.style.transform = 'scale(1)';
                    dragHandle.style.opacity = '0.85';
                }
            });
        }
        if (resizeHandle) {
            resizeHandle.addEventListener('mouseenter', () => {
                resizeHandle.style.transform = 'scale(1.1)';
                resizeHandle.style.opacity = '1';
            });
            resizeHandle.addEventListener('mouseleave', () => {
                if (!this.isResizing) {
                    resizeHandle.style.transform = 'scale(1)';
                    resizeHandle.style.opacity = '0.85';
                }
            });
        }
    }

    startDrag(e) {
        if (!this.targetElement) return;

        this.isDragging = true;
        this.startX = e.clientX;
        this.startY = e.clientY;

        const rect = this.targetElement.getBoundingClientRect();
        this.initialX = rect.left;
        this.initialY = rect.top;

        const dragHandle = this.querySelector('#drag-handle');
        if (dragHandle) {
            dragHandle.style.transform = 'scale(0.95)';
            dragHandle.style.cursor = 'grabbing';
        }

        // Disable transitions during drag
        if (this.targetElement instanceof HTMLElement) {
            this.targetElement.style.transition = 'none';
        }

        e.preventDefault();
        e.stopPropagation();

        console.log('üîß WB Resize: Drag started');
    }

    startResize(e) {
        if (!this.targetElement) return;

        this.isResizing = true;
        this.startX = e.clientX;
        this.startY = e.clientY;

        const rect = this.targetElement.getBoundingClientRect();
        this.initialWidth = rect.width;
        this.initialHeight = rect.height;

        const resizeHandle = this.querySelector('#resize-handle');
        if (resizeHandle) resizeHandle.style.transform = 'scale(0.95)';

        // Disable transitions during resize
        if (this.targetElement instanceof HTMLElement) {
            this.targetElement.style.transition = 'none';
        }

        e.preventDefault();
        e.stopPropagation();

        console.log('üîß WB Resize: Resize started');
    }

    onMouseMove(e) {
        if (this.isDragging && this.targetElement instanceof HTMLElement) {
            const deltaX = e.clientX - this.startX;
            const deltaY = e.clientY - this.startY;
            this.targetElement.style.left = (this.initialX + deltaX) + 'px';
            this.targetElement.style.top = (this.initialY + deltaY) + 'px';
        } else if (this.isResizing && this.targetElement instanceof HTMLElement) {
            const deltaX = e.clientX - this.startX;
            const deltaY = e.clientY - this.startY;
            // Calculate new dimensions
            const newWidth = Math.max(200, this.initialWidth + deltaX);
            const newHeight = Math.max(100, this.initialHeight + deltaY);
            this.targetElement.style.width = newWidth + 'px';
            this.targetElement.style.height = newHeight + 'px';
        }
    }

    onMouseUp() {
        if (this.isDragging) {
            this.isDragging = false;

            const dragHandle = this.querySelector('#drag-handle');
            if (dragHandle) {
                dragHandle.style.transform = 'scale(1)';
                dragHandle.style.cursor = 'grab';
            }

            // Re-enable transitions
            if (this.targetElement instanceof HTMLElement) {
                this.targetElement.style.transition = '';
            }

            // Save position
            const rect = this.targetElement.getBoundingClientRect();
            localStorage.setItem(`wb-resize-${this.targetElement.id || 'element'}-x`, String(rect.left));
            localStorage.setItem(`wb-resize-${this.targetElement.id || 'element'}-y`, String(rect.top));

            console.log('üîß WB Resize: Drag completed');

            // Dispatch event
            this.dispatchEvent(new CustomEvent('wb:resize:moved', {
                bubbles: true,
                detail: { x: rect.left, y: rect.top }
            }));
        }

        if (this.isResizing) {
            this.isResizing = false;

            const resizeHandle = this.querySelector('#resize-handle');
            if (resizeHandle) resizeHandle.style.transform = 'scale(1)';

            // Re-enable transitions
            if (this.targetElement instanceof HTMLElement) {
                this.targetElement.style.transition = '';
            }

            // Save dimensions
            const rect = this.targetElement.getBoundingClientRect();
            localStorage.setItem(`wb-resize-${this.targetElement.id || 'element'}-width`, String(rect.width));
            localStorage.setItem(`wb-resize-${this.targetElement.id || 'element'}-height`, String(rect.height));

            console.log('üîß WB Resize: Resize completed');

            // Dispatch event
            this.dispatchEvent(new CustomEvent('wb:resize:resized', {
                bubbles: true,
                detail: { width: rect.width, height: rect.height }
            }));
        }
    }

    // Public API
    restoreState() {
        if (!this.targetElement) return;

        const elementId = this.targetElement.id || 'element';
        const savedX = localStorage.getItem(`wb-resize-${elementId}-x`);
        const savedY = localStorage.getItem(`wb-resize-${elementId}-y`);
        const savedWidth = localStorage.getItem(`wb-resize-${elementId}-width`);
        const savedHeight = localStorage.getItem(`wb-resize-${elementId}-height`);

        if (!this.targetElement || !(this.targetElement instanceof HTMLElement)) return;

        if (savedX) this.targetElement.style.left = savedX + 'px';
        if (savedY) this.targetElement.style.top = savedY + 'px';
        if (savedWidth) this.targetElement.style.width = savedWidth + 'px';
        if (savedHeight) this.targetElement.style.height = savedHeight + 'px';
    }
}

// Register the custom element
if (!customElements.get('wb-resize')) {
    customElements.define('wb-resize', WBResizePanel);
    console.log('‚úÖ WB Resize: Component registered');
}

import { loadComponentCSS } from '../wb-css-loader/wb-css-loader.js';

// wb-resize-updown.js
// Vertical resize handle component for resizing parent containers up/down

class WBResizeUpDown extends HTMLElement {
    constructor() {
        super();
    }

    async connectedCallback() {
        await loadComponentCSS(this, 'wb-resize-updown.css');
        this.init();
    }

    init() {
        this.attachShadow({ mode: 'open' });

        // State
        this.isResizing = false;
        this.startY = 0;
        this.startHeight = 0;
        this.targetElement = null;

        // Configuration
        this.minHeight = 100;
        this.maxHeight = 600;
        this.storageKey = null;
    }

    connectedCallback() {
        // Find target BEFORE rendering to prevent scroll issues
        this.findTargetElement();

        this.render();
        this.setupEventListeners();

        // Delay loading saved height to prevent scroll jump
        requestAnimationFrame(() => {
            requestAnimationFrame(() => {
                this.loadSavedHeight();
            });
        });

        console.log('‚úÖ wb-resize-updown: Connected');
    }

    disconnectedCallback() {
        this.cleanup();
    }

    static get observedAttributes() {
        return ['target', 'min-height', 'max-height', 'storage-key', 'handle-height'];
    }

    attributeChangedCallback(name, oldValue, newValue) {
        if (oldValue === newValue) return;

        switch(name) {
            case 'target':
                this.findTargetElement();
                break;
            case 'min-height':
                this.minHeight = parseInt(newValue) || 100;
                break;
            case 'max-height':
                this.maxHeight = parseInt(newValue) || 600;
                break;
            case 'storage-key':
                this.storageKey = newValue;
                this.loadSavedHeight();
                break;
            case 'handle-height':
                this.updateHandleHeight(newValue);
                break;
        }
    }

    render() {
        const handleHeight = this.getAttribute('handle-height') || '40px';

        this.shadowRoot.innerHTML = `
            <style>
                :host {
                    display: block;
                    position: relative;
                    width: 100%;
                    height: ${handleHeight};
                    z-index: 1000;
                }

                .resize-handle {
                    position: absolute;
                    top: 0;
                    left: 0;
                    right: 0;
                    height: 100%;
                    cursor: ns-resize;
                    transition: background 0.2s;
                }

                .resize-handle:hover {
                    background: rgba(99, 102, 241, 0.1);
                }

                .resize-handle.resizing {
                    background: rgba(99, 102, 241, 0.2);
                }
            </style>

            <div class="resize-handle" part="handle" title="Drag up/down to resize"></div>
        `;
    }

    updateHandleHeight(height) {
        const handle = this.shadowRoot.querySelector('.resize-handle');
        if (handle) {
            this.style.height = height;
        }
    }

    findTargetElement() {
        const targetSelector = this.getAttribute('target');

        if (!targetSelector) {
            // Default: resize parent element
            this.targetElement = this.parentElement;
            console.log('üìê wb-resize-updown: Targeting parent element');
            return;
        }

        // Try to find target by selector
        this.targetElement = document.querySelector(targetSelector);

        if (!this.targetElement) {
            console.warn(`‚ö†Ô∏è wb-resize-updown: Target element not found: ${targetSelector}`);
            this.targetElement = this.parentElement;
        } else {
            console.log(`üìê wb-resize-updown: Targeting element: ${targetSelector}`);
        }
    }

    setupEventListeners() {
        const handle = this.shadowRoot.querySelector('.resize-handle');

        if (!handle) {
            console.error('‚ùå wb-resize-updown: Handle not found');
            return;
        }

        handle.addEventListener('mousedown', this.handleMouseDown.bind(this));
        document.addEventListener('mousemove', this.handleMouseMove.bind(this));
        document.addEventListener('mouseup', this.handleMouseUp.bind(this));
    }

    cleanup() {
        document.removeEventListener('mousemove', this.handleMouseMove);
        document.removeEventListener('mouseup', this.handleMouseUp);
    }

    handleMouseDown(e) {
        if (!this.targetElement) {
            this.findTargetElement();
        }

        if (!this.targetElement) {
            console.error('‚ùå wb-resize-updown: No target element to resize');
            return;
        }

        this.isResizing = true;
        this.startY = e.clientY;
        this.startHeight = this.targetElement.offsetHeight;

        document.body.style.cursor = 'ns-resize';
        document.body.style.userSelect = 'none';

        const handle = this.shadowRoot.querySelector('.resize-handle');
        handle.classList.add('resizing');

        e.preventDefault();

        // Dispatch resize start event
        this.dispatchEvent(new CustomEvent('wb:resize-start', {
            bubbles: true,
            composed: true,
            detail: {
                startHeight: this.startHeight,
                startY: this.startY
            }
        }));

        console.log('üñ±Ô∏è wb-resize-updown: Started dragging at Y:', this.startY, 'Height:', this.startHeight);
    }

    handleMouseMove(e) {
        if (!this.isResizing || !this.targetElement) return;

        // Drag UP (toward top) = increase height
        // Since element is at bottom, dragging up makes it taller
        const deltaY = this.startY - e.clientY;
        const newHeight = this.startHeight + deltaY;

        // Constrain height
        const constrainedHeight = Math.max(this.minHeight, Math.min(this.maxHeight, newHeight));

        this.targetElement.style.height = constrainedHeight + 'px';

        // Shrink previous sibling if it exists
        const prevSibling = this.targetElement.previousElementSibling;
        if (prevSibling && prevSibling.style.flex) {
            // If the previous element uses flexbox, it will automatically shrink
            // No action needed
        }

        // Dispatch resize event
        this.dispatchEvent(new CustomEvent('wb:resizing', {
            bubbles: true,
            composed: true,
            detail: {
                height: constrainedHeight,
                deltaY: deltaY
            }
        }));
    }

    handleMouseUp(e) {
        if (!this.isResizing) return;

        this.isResizing = false;
        document.body.style.cursor = '';
        document.body.style.userSelect = '';

        const handle = this.shadowRoot.querySelector('.resize-handle');
        handle.classList.remove('resizing');

        // Save height if storage key provided
        if (this.storageKey && this.targetElement) {
            const currentHeight = this.targetElement.offsetHeight;
            localStorage.setItem(this.storageKey, currentHeight);
            console.log('üíæ wb-resize-updown: Saved height:', currentHeight + 'px', 'to', this.storageKey);
        }

        // Dispatch resize end event
        const finalHeight = this.targetElement ? this.targetElement.offsetHeight : 0;
        this.dispatchEvent(new CustomEvent('wb:resize-end', {
            bubbles: true,
            composed: true,
            detail: {
                finalHeight: finalHeight
            }
        }));

        console.log('‚úÖ wb-resize-updown: Resize complete. Final height:', finalHeight + 'px');
    }

    loadSavedHeight() {
        if (!this.storageKey) return;

        const savedHeight = localStorage.getItem(this.storageKey);
        if (savedHeight && this.targetElement) {
            // Store current scroll positions BEFORE changing height
            const windowScrollY = window.scrollY;
            const docScrollTop = document.documentElement.scrollTop;

            // Change the height
            this.targetElement.style.height = savedHeight + 'px';

            // IMMEDIATELY restore scroll positions to prevent browser from scrolling
            window.scrollTo(0, windowScrollY);
            document.documentElement.scrollTop = docScrollTop;

            // Force to top if we're on initial load
            requestAnimationFrame(() => {
                window.scrollTo(0, 0);
                document.documentElement.scrollTop = 0;

                // Also force the content div to top
                const content = this.targetElement.querySelector('#event-log-content');
                if (content) {
                    content.scrollTop = 0;
                }
            });

            console.log('üìê wb-resize-updown: Loaded saved height:', savedHeight + 'px');
        }
    }

    // Public API methods
    setMinHeight(height) {
        this.minHeight = height;
        this.setAttribute('min-height', height);
    }

    setMaxHeight(height) {
        this.maxHeight = height;
        this.setAttribute('max-height', height);
    }

    setTarget(selector) {
        this.setAttribute('target', selector);
    }

    getTargetHeight() {
        return this.targetElement ? this.targetElement.offsetHeight : 0;
    }
}

// Register the component
customElements.define('wb-resize-updown', WBResizeUpDown);

console.log('‚úÖ wb-resize-updown component registered');

export default WBResizeUpDown;

import { loadComponentCSS } from '../wb-css-loader/wb-css-loader.js';

/**
 * WB Search Component
 * A reusable search component with modal and button variants
 */
class WBSearch extends HTMLElement {
    constructor() {
        super();
        this.isInitialized = false;
        this.searchData = [];
    }

    async connectedCallback() {
        if (!this.isInitialized) {
            await loadComponentCSS(this, 'wb-search.css');
            this.init();
        }
    }

    async init() {
        try {
            // CSS already loaded in connectedCallback
            this.render();
            this.setupEventListeners();
            this.isInitialized = true;

            this.dispatchEvent(new CustomEvent('wb-search-ready'));
            console.log('‚úÖ WB Search component initialized');
        } catch (error) {
            console.error('‚ùå Failed to initialize WB Search:', error);
        }
    }

    async loadCSS() {
        if (window.WBComponentUtils) {
            const cssPath = window.WBComponentUtils.getPath('wb-search.js', '../components/wb-search/') + 'wb-search.css';
            window.WBComponentUtils.loadCSS('wb-search', cssPath);
        } else {
            // Fallback for when WBComponentUtils is not available
            const cssPath = 'components/wb-search/wb-search.css';
            if (!document.querySelector(`link[href="${cssPath}"]`)) {
                const link = document.createElement('link');
                link.rel = 'stylesheet';
                link.href = cssPath;
                document.head.appendChild(link);
            }
        }
    }

    render() {
        const variant = this.getAttribute('variant') || 'button';
        const buttonText = this.getAttribute('button-text') || 'Search';
        const buttonIcon = this.getAttribute('button-icon') || 'üîç';
        const placeholder = this.getAttribute('placeholder') || 'Search website...';

        this.innerHTML = `
            ${this.getButtonHTML(variant, buttonText, buttonIcon)}
            ${this.getModalHTML(placeholder)}
        `;
    }

    getButtonHTML(variant, buttonText, buttonIcon) {
        if (variant === 'floating') {
            return `
                <button class="wb-search-floating" data-action="open-search">
                    <span class="wb-search-icon">${buttonIcon}</span>
                </button>
            `;
        }

        if (variant === 'icon-only') {
            return `
                <button class="wb-search-button wb-search-icon-only" data-action="open-search">
                    <span class="wb-search-icon">${buttonIcon}</span>
                </button>
            `;
        }

        return `
            <button class="wb-search-button" data-action="open-search">
                <span class="wb-search-icon">${buttonIcon}</span>
                <span class="wb-search-text">${buttonText}</span>
            </button>
        `;
    }

    getModalHTML(placeholder) {
        return `
            <div class="wb-search-modal" data-modal="search">
                <div class="wb-search-container">
                    <button class="wb-search-close" data-action="close-search">‚úï</button>
                    <input type="text" class="wb-search-input" placeholder="${placeholder}" data-search-input>
                    <div class="wb-search-results" data-search-results>
                        <div class="wb-search-no-results">Start typing to search...</div>
                    </div>
                </div>
            </div>
        `;
    }

    setupEventListeners() {
        // Open search modal
        this.querySelectorAll('[data-action="open-search"]').forEach(button => {
            button.addEventListener('click', () => this.openSearch());
        });

        // Close search modal
        this.querySelectorAll('[data-action="close-search"]').forEach(button => {
            button.addEventListener('click', () => this.closeSearch());
        });

        // Search input
        const searchInput = this.querySelector('[data-search-input]');
        if (searchInput) {
            searchInput.addEventListener('input', (e) => this.performSearch(e.target.value));
        }

        // Modal backdrop click
        const modal = this.querySelector('[data-modal="search"]');
        if (modal) {
            modal.addEventListener('click', (e) => {
                if (e.target === modal) {
                    this.closeSearch();
                }
            });
        }

        // Keyboard shortcuts
        document.addEventListener('keydown', (e) => {
            if (e.key === 'Escape' && this.isModalOpen()) {
                this.closeSearch();
            }
            if ((e.ctrlKey || e.metaKey) && e.key === 'k') {
                e.preventDefault();
                this.openSearch();
            }
        });
    }

    openSearch() {
        const modal = this.querySelector('[data-modal="search"]');
        const input = this.querySelector('[data-search-input]');

        if (modal && input) {
            modal.classList.add('wb-search-active');
            input.focus();
            document.body.style.overflow = 'hidden';

            this.dispatchEvent(new CustomEvent('wb-search-opened'));
        }
    }

    closeSearch() {
        const modal = this.querySelector('[data-modal="search"]');
        const input = this.querySelector('[data-search-input]');
        const results = this.querySelector('[data-search-results]');

        if (modal) {
            modal.classList.remove('wb-search-active');
            document.body.style.overflow = '';

            if (input) input.value = '';
            if (results) results.innerHTML = '<div class="wb-search-no-results">Start typing to search...</div>';

            this.dispatchEvent(new CustomEvent('wb-search-closed'));
        }
    }

    performSearch(query) {
        const resultsContainer = this.querySelector('[data-search-results]');
        if (!resultsContainer) return;

        if (!query.trim()) {
            resultsContainer.innerHTML = '<div class="wb-search-no-results">Start typing to search...</div>';
            return;
        }

        // Get search data from attribute or use default
        const searchData = this.getSearchData();
        const results = this.filterResults(searchData, query);

        if (results.length === 0) {
            resultsContainer.innerHTML = '<div class="wb-search-no-results">No results found</div>';
            return;
        }

        resultsContainer.innerHTML = results.map(result => `
            <div class="wb-search-result" data-result="${result.id}" onclick="this.closest('wb-search').selectResult('${result.id}')">
                <div class="wb-search-result-header">
                    <span class="wb-search-result-title">${this.highlightQuery(result.title, query)}</span>
                    <span class="wb-search-result-type">${result.type}</span>
                </div>
                <div class="wb-search-result-content">${this.highlightQuery(result.content, query)}</div>
            </div>
        `).join('');

        this.dispatchEvent(new CustomEvent('wb-search-results', {
            detail: { query, results }
        }));
    }

    getSearchData() {
        // Try to get data from data-search-data attribute
        const dataAttr = this.getAttribute('data-search-data');
        if (dataAttr) {
            try {
                return JSON.parse(dataAttr);
            } catch (e) {
                console.warn('Invalid search data JSON:', e);
            }
        }

        // Default search data
        return [
            { id: 'components', title: 'Components', type: 'Section', content: 'Web components for building interfaces' },
            { id: 'wb-button', title: 'WB Button', type: 'Component', content: 'Customizable button component with variants' },
            { id: 'wb-search', title: 'WB Search', type: 'Component', content: 'Search component with modal and button variants' },
            { id: 'theme-system', title: 'Theme System', type: 'Feature', content: 'Dark and light theme support' },
            { id: 'plugins', title: 'Plugins', type: 'System', content: 'Extensible plugin architecture' }
        ];
    }

    filterResults(data, query) {
        const normalizedQuery = query.toLowerCase();
        return data.filter(item => 
            item.title.toLowerCase().includes(normalizedQuery) ||
            item.content.toLowerCase().includes(normalizedQuery) ||
            (item.tags && item.tags.some(tag => tag.toLowerCase().includes(normalizedQuery)))
        );
    }

    highlightQuery(text, query) {
        if (!query.trim()) return text;

        const regex = new RegExp(`(${query.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')})`, 'gi');
        return text.replace(regex, '<mark class="wb-search-highlight">$1</mark>');
    }

    selectResult(resultId) {
        const searchData = this.getSearchData();
        const result = searchData.find(item => item.id === resultId);

        if (result) {
            this.dispatchEvent(new CustomEvent('wb-search-select', {
                detail: { result }
            }));

            // Default behavior - close search
            this.closeSearch();
        }
    }

    isModalOpen() {
        const modal = this.querySelector('[data-modal="search"]');
        return modal && modal.classList.contains('wb-search-active');
    }

    // Public API
    setSearchData(data) {
        this.searchData = data;
        this.setAttribute('data-search-data', JSON.stringify(data));
    }

    getSearchQuery() {
        const input = this.querySelector('[data-search-input]');
        return input ? input.value : '';
    }

    focusSearch() {
        this.openSearch();
    }
